import { useContext } from 'react';
import classes from './CartModal.module.css';
import CardContext from '../../context/CardContext';

export default function CartModal({ onClose }) {
const value =useContext(CardContext);
   const removeItemHandler =(id)=>{
       value.removeItem(id)
   }
   const addItemHandler=(item)=>{
        const newItem = { ...item, quantity: 1 };
        value.addItem(newItem);
   }
   
  return (
    <>
      {/* Backdrop */}
      <div className={classes.backdrop} onClick={onClose}></div>

      {/* Modal Box */}
      <div className={classes.modal}>
        <ul>
          {value.items.map(item => (
            <li key={item.id}>
              {item.name} (x{item.quantity}) - ${item.price} (Total: ${(item.quantity * item.price).toFixed(2)})
              <button className={classes.remove} type='button' onClick={() => removeItemHandler(item.id)} >Remove</button>
            <button className={classes.remove} type='button' onClick={() => addItemHandler(item)} >add</button>
            </li>
          ))}
        </ul>

        <div className={classes.total}>
          <span>Total Amount</span>
          <span>${value.totalAmount.toFixed(2)}</span>
        </div>

        <div className={classes.actions}>
          <button className={classes['button--alt']} onClick={onClose}>Cancel</button>
          <button className={classes.button}>Order</button>
        </div>
      </div>
    </>
  )
}
