import classes from './CartModal.module.css';

export default function CartModal({ onClose }) {
  // Abhi static data
  const DUMMY_ITEMS = [
    { id: 'm1', name: 'Sushi', amount: 2, price: 12.99 },
    { id: 'm2', name: 'Burger', amount: 1, price: 8.50 },
  ];

  const totalAmount = DUMMY_ITEMS.reduce((acc, item) => acc + item.price * item.amount, 0).toFixed(2);

  return (
    <>
      {/* Backdrop */}
      <div className={classes.backdrop} onClick={onClose}></div>

      {/* Modal Box */}
      <div className={classes.modal}>
        <ul>
          {DUMMY_ITEMS.map(item => (
            <li key={item.id}>
              {item.name} (x{item.amount}) - ${item.price}
            </li>
          ))}
        </ul>

        <div className={classes.total}>
          <span>Total Amount</span>
          <span>${totalAmount}</span>
        </div>

        <div className={classes.actions}>
          <button className={classes['button--alt']} onClick={onClose}>Cancel</button>
          <button className={classes.button}>Order</button>
        </div>
      </div>
    </>
  )
}
